import '../models/chip_item_model.dart';import 'package:equatable/equatable.dart';import 'package:flutter/material.dart';import '/core/app_export.dart';import '../models/chip_item_model.dart';import 'package:your_social_now/presentation/choose_your_interest_screen/models/choose_your_interest_model.dart';part 'choose_your_interest_event.dart';part 'choose_your_interest_state.dart';class ChooseYourInterestBloc extends Bloc<ChooseYourInterestEvent, ChooseYourInterestState> {ChooseYourInterestBloc(ChooseYourInterestState initialState) : super(initialState) { on<ChooseYourInterestInitialEvent>(_onInitialize); on<UpdateChipViewEvent>(_updateChipView); }

_onInitialize(ChooseYourInterestInitialEvent event, Emitter<ChooseYourInterestState> emit, ) async  { emit(state.copyWith(chooseYourInterestModelObj: state.chooseYourInterestModelObj?.copyWith(chipItemList: fillChipItemList()))); } 
_updateChipView(UpdateChipViewEvent event, Emitter<ChooseYourInterestState> emit, ) { List<ChipItemModel> newList = List<ChipItemModel>.from(state.chooseYourInterestModelObj!.chipItemList); newList[event.index] = newList[event.index].copyWith(isSelected: event.isSelected); emit(state.copyWith(chooseYourInterestModelObj: state.chooseYourInterestModelObj?.copyWith(chipItemList: newList))); } 
List<ChipItemModel> fillChipItemList() { return List.generate(20, (index) => ChipItemModel()); } 
 }
